DELIMITER $$
--
-- Procedimiento para listar promociones con filtrado / paginado
--
CREATE PROCEDURE PA_productSearchPromo(
  IN plan_pre_id INT,
  IN plan_post_id INT,
  IN affiliation_id INT,
